SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS compl_pessoa_fisica_delete ON compl_pessoa_fisica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_compl_pessoa_fisica_delete() RETURNS trigger AS $BODY$
declare
reg_integracao_p		gerar_int_padrao.reg_integracao;
nr_prontuario_w			pessoa_fisica.nr_prontuario%type;
ie_funcionario_w		pessoa_fisica.ie_funcionario%type;
nr_seq_conselho_w		pessoa_fisica.nr_seq_conselho%type;
ds_sep_bv_w				varchar(100);
ds_param_integ_hl7_w	varchar(4000);
BEGIN
  BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

BEGIN
ds_sep_bv_w := obter_separador_bv;
select nr_prontuario,
		ie_funcionario,
		nr_seq_conselho
into STRICT nr_prontuario_w,
	ie_funcionario_w,
	nr_seq_conselho_w
from pessoa_fisica
where cd_pessoa_fisica = OLD.cd_pessoa_fisica;
exception
when others then
	nr_prontuario_w	:= null;
end;	

reg_integracao_p.ie_operacao		:=	'A';
reg_integracao_p.nr_prontuario      :=   nr_prontuario_w;
reg_integracao_p.cd_pessoa_fisica      :=   OLD.cd_pessoa_fisica;
reg_integracao_p.ie_funcionario 	:= ie_funcionario_w;
reg_integracao_p.nr_seq_conselho	:= nr_seq_conselho_w;

SELECT CASE WHEN COUNT(*)=0 THEN  'N'  ELSE 'S' END
  INTO STRICT reg_integracao_p.ie_pessoa_atend
  FROM pessoa_fisica_aux
 WHERE cd_pessoa_fisica = OLD.cd_pessoa_fisica
   AND nr_primeiro_atend IS NOT NULL;

if OLD.nr_cpf is not null then
   reg_integracao_p.ie_possui_cpf := 'S';
end if;

if (wheb_usuario_pck.get_ie_lote_contabil = 'N') then
	reg_integracao_p := gerar_int_padrao.gravar_integracao('12', OLD.cd_pessoa_fisica, OLD.nm_usuario, reg_integracao_p);
end if;

CALL send_physician_intpd(OLD.cd_pessoa_fisica, OLD.nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, 'U');
if (wheb_usuario_pck.is_evento_ativo(853) = 'S') then
  CALL enviar_pf_medico_hl7(OLD.cd_pessoa_fisica,'MUP');
end if;

delete from compl_pessoa_fisica_setor where nr_seq_compl_pessoa_fisica = OLD.nr_sequencia and cd_pessoa_fisica = OLD.cd_pessoa_fisica;

end if;
  END;
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_compl_pessoa_fisica_delete() FROM PUBLIC;

CREATE TRIGGER compl_pessoa_fisica_delete
	AFTER DELETE ON compl_pessoa_fisica FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_compl_pessoa_fisica_delete();

