SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS compl_pessoa_fisica_update_hl7 ON compl_pessoa_fisica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_compl_pessoa_fisica_update_hl7() RETURNS trigger AS $BODY$
declare

ds_sep_bv_w		varchar(100);
ds_param_integ_hl7_w	varchar(4000);
qt_atendimento_w     bigint;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S' ) then

	ds_sep_bv_w := obter_separador_bv;
	
	if (coalesce(OLD.ds_endereco,'X') 	<> coalesce(NEW.ds_endereco,'X')) 	or (coalesce(OLD.ds_municipio,'X') <> coalesce(NEW.ds_municipio,'X')) 	or (coalesce(OLD.sg_estado,'X') 	<> coalesce(NEW.sg_estado,'X')) 	or (coalesce(OLD.cd_cep,'X') 		<> coalesce(NEW.cd_cep,'X')) 		or (coalesce(OLD.nr_ddi_telefone,'X') 	<> coalesce(NEW.nr_ddi_telefone,'X')) or (coalesce(OLD.nr_ddd_telefone,'X') 	<> coalesce(NEW.nr_ddd_telefone,'X')) or (coalesce(OLD.nr_telefone,'X') 	<> coalesce(NEW.nr_telefone,'X')) 	then
		BEGIN
		
		ds_param_integ_hl7_w :=	'cd_pessoa_fisica=' || NEW.cd_pessoa_fisica || ds_sep_bv_w;
		CALL gravar_agend_integracao(145, ds_param_integ_hl7_w);
		
	--HCOR ADT A08


		if (wheb_usuario_pck.is_evento_ativo(783) = 'S') then

			qt_atendimento_w	:= obter_qtd_atendimentos(NEW.cd_pessoa_fisica);
		
			if (qt_atendimento_w > 0) then
				ds_param_integ_hl7_w := 'cd_pessoa_fisica=' || NEW.cd_pessoa_fisica || ds_sep_bv_w;
				CALL gravar_agend_integracao(783, ds_param_integ_hl7_w);
			end if;
		end if;

		-- For CareStream Interface, Japan

        CALL call_interface_file(933, 'carestream_ris_japan_l10n_pck.patient_change_info( ' || NEW.cd_pessoa_fisica || ', ''' || NEW.nm_usuario || ''' , 0 , null, ' || obter_estabelecimento_ativo || ');' , NEW.nm_usuario);

		end;
	end if;
	
end if;

    --Outbound ADT_A08

	CALL call_bifrost_content('patient.registration.update','person_json_pck.get_patient_message_clob('||NEW.cd_pessoa_fisica||')', NEW.nm_usuario);

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_compl_pessoa_fisica_update_hl7() FROM PUBLIC;

CREATE TRIGGER compl_pessoa_fisica_update_hl7
	BEFORE UPDATE ON compl_pessoa_fisica FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_compl_pessoa_fisica_update_hl7();

