DROP TRIGGER IF EXISTS componente_kit_atual ON componente_kit CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_componente_kit_atual() RETURNS trigger AS $BODY$
declare

nr_seq_interna_w	bigint;

BEGIN

if (obter_se_prot_lib_regras = 'S') then
	select	nr_seq_protocolo
	into STRICT	nr_seq_interna_w
	from	kit_material
	where	cd_kit_material	= NEW.cd_kit_material;

	update	protocolo_medicacao
	set	nm_usuario_aprov	 = NULL,
		dt_aprovacao		 = NULL,
		ie_status		=	'PA'
	where	nr_seq_interna		=	nr_seq_interna_w;
end if;

RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_componente_kit_atual() FROM PUBLIC;

CREATE TRIGGER componente_kit_atual
	BEFORE INSERT OR UPDATE ON componente_kit FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_componente_kit_atual();

