SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS conta_paciente_delete ON conta_paciente CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_conta_paciente_delete() RETURNS trigger AS $BODY$
declare
	 
ds_module_tasy_w		varchar(255);
osuser_tasy_w			varchar(100);
ds_origem_w			varchar(1500);
qt_hist_laudo_w			bigint := 0;
 
BEGIN 
 
select	max(module||' - ' || machine||' - ' || program|| ' - ' || OSUSER|| ' - ' || TERMINAL), 
	max(substr(OSUSER,1,15)) 
into STRICT	ds_module_tasy_w, 
	osuser_tasy_w 
from	v$session 
where	audsid = (SELECT userenv('sessionid') ) 
and 	upper(program) like '%TASY%';
 
if (ds_module_tasy_w is not null) then 
	ds_origem_w := substr(dbms_utility.format_call_stack,1,1500);	
end if;
 
select	count(1) 
into STRICT	qt_hist_laudo_w 
from	sus_hist_envio_laudo_pac 
where	nr_interno_conta = OLD.nr_interno_conta  LIMIT 1;
 
if (qt_hist_laudo_w > 0) then 
	BEGIN 
	 
	update	sus_hist_envio_laudo_pac 
	set 	nr_interno_conta  = NULL 
	where	nr_interno_conta = OLD.nr_interno_conta;
	 
	end;
end if;
 
CALL same_excluir_conta_pront(OLD.nr_interno_conta, OLD.nr_atendimento, wheb_usuario_pck.get_nm_usuario);
 
RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_conta_paciente_delete() FROM PUBLIC;

CREATE TRIGGER conta_paciente_delete
	BEFORE DELETE ON conta_paciente FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_conta_paciente_delete();

