SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS contrato_insert ON contrato CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_contrato_insert() RETURNS trigger AS $BODY$
DECLARE
qt_reg_w	smallint;
ie_status_w  bigint;
BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'N')  then
	goto Final;
end if;

select count(*)
into STRICT ie_status_w
from contrato_regra_nf
where nr_seq_contrato = NEW.nr_sequencia and obter_bloq_canc_proj_rec(nr_seq_proj_rec) > 0;

if (ie_status_w > 0) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1144309);  -- Registro associado a um projeto bloqueado ou cancelado. 
end if;

if (NEW.cd_cgc_contratado is not null) and (NEW.cd_pessoa_contratada is not null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278457);
end if;

if (NEW.cd_cgc_contratado is null) and (NEW.cd_pessoa_contratada is null) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(278459);
end if;
<<Final>>
qt_reg_w	:= 0;


RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_contrato_insert() FROM PUBLIC;

CREATE TRIGGER contrato_insert
	BEFORE INSERT OR UPDATE ON contrato FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_contrato_insert();

