DROP TRIGGER IF EXISTS contrato_regra_nf_afupd ON contrato_regra_nf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_contrato_regra_nf_afupd() RETURNS trigger AS $BODY$
DECLARE
C01 CURSOR FOR
    SELECT NR_SEQUENCIA, PR_TRIBUTO 
    from CONTRATO_REGRA_PAGTO_TRIB
    where NR_SEQ_REGRA_NF = OLD.nr_sequencia;

    ContRegPagTrib C01%Rowtype;

BEGIN
IF (wheb_usuario_pck.get_ie_executar_trigger = 'S') THEN
    IF (NEW.VL_PAGTO <> OLD.VL_PAGTO) THEN
        open C01;
        loop
        fetch C01 into
            ContRegPagTrib;
        EXIT WHEN NOT FOUND; /* apply on C01 */
            BEGIN
                UPDATE CONTRATO_REGRA_PAGTO_TRIB
                SET VL_TRIBUTO = NEW.VL_PAGTO / 100 * ContRegPagTrib.PR_TRIBUTO
                Where NR_SEQUENCIA = ContRegPagTrib.NR_SEQUENCIA;
            end;
        end loop;
        close C01;	
    END IF;
END IF;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
