SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS contrato_regra_nf_atual ON contrato_regra_nf CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_contrato_regra_nf_atual() RETURNS trigger AS $BODY$
DECLARE

cd_estabelecimento_w		bigint;
ds_erro_w					varchar(2000);
ie_situacao_w				MATERIAL.IE_SITUACAO%type;


BEGIN

if (obter_bloq_canc_proj_rec(NEW.nr_seq_proj_rec) > 0) then
    CALL wheb_mensagem_pck.exibir_mensagem_abort(1144309);  -- registro associado a um projeto bloqueado ou cancelado. 
end if;

CALL CTB_Consistir_Conta_Titulo(NEW.cd_conta_contabil, WHEB_MENSAGEM_PCK.get_texto(807458)/*'Conta contabil'*/
);
select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	contrato
where	nr_sequencia		= NEW.nr_seq_contrato;
CTB_Consistir_Conta_estab(cd_estabelecimento_w, NEW.cd_conta_contabil, WHEB_MENSAGEM_PCK.get_texto(807458)/*'Conta contabil'*/
);

ds_erro_w := con_consiste_vigencia_conta(NEW.cd_conta_contabil, NEW.dt_inicio_vigencia, NEW.dt_fim_vigencia, ds_erro_w);
if (ds_erro_w is not null) then
CALL wheb_mensagem_pck.exibir_mensagem_abort(190916,'DS_ERRO_P='||DS_ERRO_W);
end if;

select	coalesce(max(ie_situacao),'I')
into STRICT	ie_situacao_w
from	material
where	cd_material = NEW.cd_material;

if (ie_situacao_w = 'I') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(819418);
end if;

RETURN NEW;
END;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_contrato_regra_nf_atual() FROM PUBLIC;

CREATE TRIGGER contrato_regra_nf_atual
	BEFORE INSERT OR UPDATE ON contrato_regra_nf FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_contrato_regra_nf_atual();

