CREATE OR REPLACE FUNCTION trigger_fct_controla_registros_transacao() RETURNS trigger AS $BODY$
declare

cont_w   smallint := 0;

BEGIN

 select coalesce(count(*),0)
 into STRICT   cont_w
 from  pls_parametro_escrit_quota;

