CREATE OR REPLACE FUNCTION trigger_fct_convenio_receb_befinsert() RETURNS trigger AS $BODY$
declare

ie_vl_conv_rec_adic_w	varchar(1);

BEGIN

select 	coalesce(max(ie_vl_conv_rec_adic),'N')
into STRICT	ie_vl_conv_rec_adic_w
from 	parametro_contas_receber
where 	cd_estabelecimento	= NEW.cd_estabelecimento;

ie_vl_conv_rec_adic_w	:= coalesce(OBTER_SE_ATUALIZA_JUROS_RECEB(NEW.cd_convenio,NEW.dt_recebimento), ie_vl_conv_rec_adic_w);

if (ie_vl_conv_rec_adic_w = 'O') then
	NEW.ie_deduzir_adic	:= 'N';
end if;

RETURN NEW;
end
