SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS convenio_retorno_item_delete ON convenio_retorno_item CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_convenio_retorno_item_delete() RETURNS trigger AS $BODY$
DECLARE
cont_proc_w		bigint := 0;
cont_mat_w		bigint := 0;
ie_conv_lib_rep_w	varchar(1)  := 'S';

BEGIN

ie_conv_lib_rep_w := Obter_Param_Usuario(27, 255, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.Get_cd_estabelecimento, ie_conv_lib_rep_w);

if (ie_conv_lib_rep_w = 'N') then

	select	count(*)
	into STRICT	cont_mat_w
	from	material_repasse a,
		material_atend_paciente b
	where	a.nr_seq_material	= b.nr_sequencia
	and	b.nr_interno_conta	= OLD.nr_interno_conta
	and	coalesce(b.nr_doc_convenio,'Não Informada')	= OLD.cd_autorizacao
	and	a.ie_status		in ('R','S','L');

	select	count(*)
	into STRICT	cont_proc_w
	from	procedimento_repasse a,
		procedimento_paciente b
	where	a.nr_seq_procedimento	= b.nr_sequencia
	and	b.nr_interno_conta	= OLD.nr_interno_conta
	and	coalesce(b.nr_doc_convenio,'Não Informada')	= OLD.cd_autorizacao
	and	a.ie_status		in ('R','S','L');

	if (cont_proc_w > 0 or cont_mat_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(238170,	'NR_INTERNO_CONTA_W='||OLD.NR_INTERNO_CONTA||
								';CD_AUTORIZACAO_W='||OLD.CD_AUTORIZACAO);
	end if;
end if;

update convenio_retorno_movto
set nr_seq_ret_item  = NULL
where nr_seq_ret_item = OLD.nr_sequencia;

RETURN OLD;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_convenio_retorno_item_delete() FROM PUBLIC;

CREATE TRIGGER convenio_retorno_item_delete
	BEFORE DELETE ON convenio_retorno_item FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_convenio_retorno_item_delete();

