SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS convenio_retorno_movto_update ON convenio_retorno_movto CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_convenio_retorno_movto_update() RETURNS trigger AS $BODY$
Declare
ie_existe_w		bigint;
cd_motivo_glosa_w	integer;
cd_convenio_w		integer;

BEGIN

if (coalesce(NEW.cd_motivo,'0') <> '0') and (coalesce(NEW.cd_motivo,'0') <> coalesce(OLD.cd_motivo,'0')) then

	select	cd_convenio
	into STRICT	cd_convenio_w
	from	convenio_retorno
	where	nr_sequencia	= NEW.nr_seq_retorno;

	select	coalesce(max(cd_motivo_glosa),0)
	into STRICT	cd_motivo_glosa_w
	from	convenio_motivo_glosa
	where	cd_convenio		= cd_convenio_w
	and	cd_glosa_convenio		= NEW.cd_motivo;

	if (cd_motivo_glosa_w <> 0) then
		NEW.cd_motivo	:= cd_motivo_glosa_w;
	elsif (cd_motivo_glosa_w = 0) then
		select	count(*)
		into STRICT	ie_existe_w
		from	motivo_glosa
		where	to_char(cd_motivo_glosa) = NEW.cd_motivo;

		if (ie_existe_w = 0) then
			/*O motivo de glosa informado não existe na tabela Motivo Glosa!
			Código: #@CD_MOTIVO#@*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(266934, 'CD_MOTIVO=' || NEW.cd_motivo);
		end if;
	end if;
end if;


RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_convenio_retorno_movto_update() FROM PUBLIC;

CREATE TRIGGER convenio_retorno_movto_update
	BEFORE UPDATE ON convenio_retorno_movto FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_convenio_retorno_movto_update();

