end if;
BEGIN
	select  1
	into STRICT	qt_proc_inativo_w
	from    proc_interno
	where   nr_sequencia = NEW.nr_seq_proc_interno
	and     ie_situacao      = 'I';


exception
when others then
	qt_proc_inativo_w := 0;
end;

if (coalesce(qt_proc_inativo_w,0) > 0) then
	--Procedimento interno inativo, verifique na função Exames e Procedimentos Internos!
