CREATE OR REPLACE FUNCTION trigger_fct_cpoe_comment_linkage_upd() RETURNS trigger AS $BODY$
DECLARE
    si_format_value_w    cpoe_std_comment.si_format_value%TYPE;
    ds_text_interface_w  varchar(20);
BEGIN
  BEGIN
    IF ( coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S' ) THEN
        IF
            NEW.ds_additional_text IS NOT NULL
            AND NEW.nr_seq_std_comment IS NOT NULL
