            FROM
                cpoe_std_comment a
            WHERE
                a.nr_sequencia = NEW.nr_seq_std_comment;

            IF si_format_value_w IS NOT NULL THEN
                BEGIN
                    NEW.ds_text_interface := obter_formato_interface(ds_valor_p => NEW.ds_additional_text,
                                                                     ie_formato_p => si_format_value_w);

                EXCEPTION
                    WHEN OTHERS THEN
                        NULL;
                END;
            END IF;

        END IF;

    END IF;
  END;
RETURN NEW;
END
$BODY$
