DROP TRIGGER IF EXISTS cpoe_dieta_insert_update ON cpoe_dieta CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cpoe_dieta_insert_update() RETURNS trigger AS $BODY$
declare
BEGIN
  BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	if ((NEW.dt_inicio is not null) and (NEW.dt_inicio <> OLD.dt_inicio) and (obter_funcao_ativa = 252)) then
		if (NEW.ie_administracao <> 'P') then
			NEW.hr_prim_horario := '00:00';
		else
			NEW.hr_prim_horario := to_char(NEW.dt_inicio, 'hh24:mi');
		end if;
	end if;

    if (NEW.hr_prim_horario is not null) and ((NEW.hr_prim_horario <> OLD.hr_prim_horario) or (OLD.dt_prim_horario is null)) then
		NEW.dt_prim_horario := to_date(to_char(coalesce(NEW.dt_inicio, LOCALTIMESTAMP),'dd/mm/yyyy') || ' ' || NEW.hr_prim_horario,'dd/mm/yyyy hh24:mi');
	end if;	
	
	CALL generate_task_needs_ack(NEW.nr_atendimento, NEW.cd_pessoa_fisica, NEW.nr_sequencia, 'D', NEW.dt_liberacao, NEW.dt_liberacao_farm, OLD.dt_liberacao_farm, NEW.dt_inicio);	
end if;
exception
	when others then
	null;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
