SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cpoe_estrutura_before_ins ON pi_estrutura_cad CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cpoe_estrutura_before_ins() RETURNS trigger AS $BODY$
declare

nr_seq_mensagem_w bigint := 1151002;
ie_existe_proc_w varchar(1);
ie_rad_or_physi_w PROC_ORDER_TYPE.NR_SEQ_ORDER_TYPE%type := 0;

BEGIN

if ((wheb_usuario_pck.get_ie_executar_trigger = 'S') and (coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'ja_JP')) then

   select coalesce(max('S'), 'N')
    into STRICT ie_existe_proc_w
    from pi_estrutura_cad c
    where
    c.nr_seq_proc_int = NEW.nr_seq_proc_int;

    if (ie_existe_proc_w = 'S') then

        select coalesce(max(1),0) 
        into STRICT ie_rad_or_physi_w 
        from PROC_ORDER_TYPE pot 
        inner join CPOE_TIPO_PEDIDO ctp on ctp.NR_SEQUENCIA = NR_SEQ_ORDER_TYPE
        where pot.NR_SEQ_PROC_INTERNO = NEW.nr_seq_proc_int 
        and ctp.NR_SEQ_SUB_GRP IN ('R', 'PH');

        if (ie_rad_or_physi_w > 0) then 
    
            CALL wheb_mensagem_pck.exibir_mensagem_abort(nr_seq_mensagem_w);

        end if;
        
    end if;

end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cpoe_estrutura_before_ins() FROM PUBLIC;

CREATE TRIGGER cpoe_estrutura_before_ins
	BEFORE INSERT ON pi_estrutura_cad FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cpoe_estrutura_before_ins();

