SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cpoe_material_delete ON cpoe_material CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cpoe_material_delete() RETURNS trigger AS $BODY$
declare

ds_erro_w		varchar(2000);
ds_retorno_w	varchar(255);
BEGIN
  BEGIN

	BEGIN
		update cpoe_revalidation_events
		set    nr_seq_material  = NULL
		where  nr_seq_material = OLD.nr_sequencia;
	
		if (cpoe_obter_se_contem_prescr(OLD.nr_sequencia,'M') = 'S') then
			ds_erro_w	:= substr(dbms_utility.format_call_stack,1,2000);
			CALL gravar_log_tasy(10007, ' CPOE_MATERIAL_DELETE_10007 :  ' ||ds_erro_w||
								   ' :old.nr_sequencia: '	||OLD.nr_sequencia ||
								   ' :old.nr_atendimento: '	||OLD.nr_atendimento||
								   ' :old.cd_pessoa_fisica: '	||OLD.cd_pessoa_fisica,
								   OLD.nm_usuario);
		end if;
	exception
	when others then
		ds_erro_w := null;
	end;

	BEGIN
		delete
		  from alerta_api
		 where nr_seq_cpoe = OLD.nr_sequencia;
	exception
	when others then
		ds_erro_w := null;
	end;
	
	BEGIN
		CALL delete_cpoe_just_interacao(OLD.nr_sequencia, OLD.cd_material, 'N');
	exception
	when others then
		ds_erro_w	:= substr(dbms_utility.format_call_stack,1,2000);
		CALL gravar_log_tasy(10007, ' CPOE_MATERIAL_DELETE_10007_2 :  ' ||ds_erro_w||
							   ' :old.nr_sequencia: '	||OLD.nr_sequencia ||
							   ' :old.nr_atendimento: '	||OLD.nr_atendimento||
							   ' :old.cd_pessoa_fisica: '	||OLD.cd_pessoa_fisica,
							   OLD.nm_usuario);
	end;

  END;
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cpoe_material_delete() FROM PUBLIC;

CREATE TRIGGER cpoe_material_delete
	BEFORE DELETE ON cpoe_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cpoe_material_delete();

