		CALL cpoe_atualizar_inf_adic(NEW.nr_atendimento, NEW.nr_sequencia, 'M', NEW.dt_liberacao_enf, NEW.dt_liberacao_farm, null, null, null, null, null, NEW.nr_seq_cpoe_order_unit);
	end if;
	exception
	when others then
		CALL gravar_log_cpoe('CPOE_MAT_INSERT_UPDATE_AFTER - CPOE_ATUALIZAR_INF_ADIC - Erro: ' || substr(sqlerrm(SQLSTATE),1,1500) || ' :new.nr_sequencia '|| NEW.nr_sequencia, NEW.nr_atendimento);
	end;

  ie_use_integration_w := obter_param_usuario(9041, 10, obter_perfil_ativo, NEW.nm_usuario, obter_estabelecimento_ativo, ie_use_integration_w);
	
  if (ie_use_integration_w = 'S')
    and ((OLD.DT_LIBERACAO IS NULL AND NEW.DT_LIBERACAO IS NOT NULL)
      or (OLD.DT_LIB_SUSPENSAO IS NULL AND NEW.DT_LIB_SUSPENSAO IS NOT NULL )) then

    if (NEW.ie_controle_tempo = 'S') then
      select obter_cpoe_regra_ator('IP')
      into STRICT ie_order_integr_type_w
;			
    else
      select obter_cpoe_regra_ator('M')
      into STRICT ie_order_integr_type_w
;
    end if;			

    if (ie_order_integr_type_w = 'OI') then
    
      nr_entity_identifier_w := cpoe_mat_json_pck.getCpoeIntegracaoAckMat(NEW.nr_sequencia, NEW.nm_usuario, nr_entity_identifier_w);

      if (OLD.DT_LIBERACAO IS NULL AND NEW.DT_LIBERACAO IS NOT NULL) then
        
        CALL call_bifrost_content('prescription.medicine.order.request','cpoe_mat_json_pck.get_message_clob(' || NEW.nr_sequencia || ', ''NW'', ' || nr_entity_identifier_w || ')', NEW.nm_usuario);

      elsif (OLD.DT_LIB_SUSPENSAO IS NULL AND NEW.DT_LIB_SUSPENSAO IS NOT NULL ) then
      
        CALL call_bifrost_content('prescription.medicine.order.request','cpoe_mat_json_pck.get_message_clob(' || NEW.nr_sequencia || ', ''CA'', ' || nr_entity_identifier_w || ')', NEW.nm_usuario);

      end if;

    end if;						

  end if;

end if;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cpoe_mat_insert_update_after() FROM PUBLIC;

CREATE TRIGGER cpoe_mat_insert_update_after
	AFTER INSERT OR UPDATE ON cpoe_material FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cpoe_mat_insert_update_after();

