SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cpoe_order_unit_update ON cpoe_order_unit CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cpoe_order_unit_update() RETURNS trigger AS $BODY$
BEGIN

	IF (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  THEN
		IF OLD.dt_liberacao IS NULL THEN
			IF TG_OP = 'UPDATE' AND NEW.SI_CPOE_TYPE_OF_ITEM IS DISTINCT FROM OLD.SI_CPOE_TYPE_OF_ITEM OR TG_OP = 'UPDATE' AND NEW.SI_TYPE_OF_PRESCRIPTION IS DISTINCT FROM OLD.SI_TYPE_OF_PRESCRIPTION OR TG_OP = 'UPDATE' AND NEW.IE_DRUG_INFO IS DISTINCT FROM OLD.IE_DRUG_INFO THEN
				IF NEW.si_cpoe_type_of_item = 'M' AND NEW.si_type_of_prescription = 'OPIHO' THEN
					UPDATE cpoe_material
					SET
						ie_drug_info = NEW.ie_drug_info
					WHERE
						nr_seq_cpoe_order_unit = NEW.nr_sequencia;

				ELSE
					UPDATE cpoe_material
					SET
						ie_drug_info  = NULL
					WHERE
						nr_seq_cpoe_order_unit = NEW.nr_sequencia;

				END IF;

			END IF;

		END IF;
	END IF;
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cpoe_order_unit_update() FROM PUBLIC;

CREATE TRIGGER cpoe_order_unit_update
	AFTER UPDATE ON cpoe_order_unit FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cpoe_order_unit_update();

