SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cpoe_procedimento_update ON cpoe_procedimento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cpoe_procedimento_update() RETURNS trigger AS $BODY$
declare

ds_stack_w				varchar(2000);
ie_possui_niveis_glic_w char(1);
cd_intervalo_w			intervalo_prescricao.cd_intervalo%type;

BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') = 'S') then

if (OLD.nr_seq_prot_glic is null) and (NEW.nr_seq_prot_glic is not null) then
	
	select	max(coalesce('S','N'))
	into STRICT	ie_possui_niveis_glic_w
	from	cpoe_proc_glic
	where	nr_seq_procedimento = NEW.nr_sequencia;
	
	if (coalesce(ie_possui_niveis_glic_w,'N') = 'N') then
		CALL cpoe_gerar_proc_glic(NEW.nr_sequencia, NEW.nr_seq_prot_glic, NEW.nm_usuario, NEW.nr_atendimento, NEW.cd_pessoa_fisica, 'N');
	end if;
	
end if;	

if (NEW.cd_intervalo is null and NEW.nr_seq_prot_glic is not null)  then	
	cd_intervalo_w := obter_intervalo_ccg(NEW.nr_seq_prot_glic);	
		
	if (cd_intervalo_w is not null) then
		NEW.cd_intervalo :=  cd_intervalo_w;
	end if;
end if;

if (NEW.ie_evento_unico = 'S') then
	if (NEW.dt_fim is null) then
		NEW.ie_duracao := 'P';
		NEW.dt_fim := (NEW.dt_inicio + 1) -1/86400;
	end if;
	
	if (NEW.dt_fim > NEW.dt_inicio + 1) then
		NEW.ie_duracao := 'P';
		NEW.dt_fim := (NEW.dt_inicio + 1) -1/86400;
	end if;
end if;

if (coalesce(OLD.cd_setor_atendimento,0) <> coalesce(NEW.cd_setor_atendimento,0)) then
	NEW.cd_setor_liberacao := null;
end if;

end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cpoe_procedimento_update() FROM PUBLIC;

CREATE TRIGGER cpoe_procedimento_update
	BEFORE UPDATE ON cpoe_procedimento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cpoe_procedimento_update();

