DROP TRIGGER IF EXISTS criterio_distr_orc_atual ON criterio_distr_orc CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_criterio_distr_orc_atual() RETURNS trigger AS $BODY$
DECLARE

qt_coluna_w		bigint;

BEGIN

if (NEW.ie_criterio_centro = 'C') then
	if (NEW.ds_view is null) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266391);
		/*O campo visão deve ser informado');*/

	end if;
	if (substr(NEW.ds_view,1,3) <> 'CUS') then
		/*'Os três primeiros caracteres devem ser CUS');*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(266392);
	end if;
	select count(*)
	into STRICT	qt_coluna_w
	from	col
	where	tname = NEW.ds_view;

	if (qt_coluna_w > 5) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(266393);
	end if;

	select count(*)
	into STRICT	qt_coluna_w
	from	col
	where	tname = NEW.ds_view
	and	cname in ('CD_ESTABELECIMENTO','DT_REFERENCIA','CD_CENTRO_CONTROLE', 'QT_DISTRIBUICAO', 'QT_PESO');
	if (qt_coluna_w > 5) then
		/*Existem campos com nome diferente do especificado no Shift F8 do campo visão');*/

		CALL wheb_mensagem_pck.exibir_mensagem_abort(266394);
	end if;

end if;

if (NEW.ie_criterio_centro <> 'C') and (OLD.ie_criterio_centro = 'C') then
	NEW.ds_view	:= '';
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
