DROP TRIGGER IF EXISTS cta_pendencia_hist_atual ON cta_pendencia_hist CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cta_pendencia_hist_atual() RETURNS trigger AS $BODY$
DECLARE
 
nr_interno_conta_w	bigint;
 
BEGIN 
 
select 	coalesce(max(nr_interno_conta),0) 
into STRICT	nr_interno_conta_w 
from 	cta_pendencia 
where 	nr_sequencia = NEW.nr_seq_pend;
 
if (nr_interno_conta_w > 0) then 
	NEW.VL_CONTA_ESTAGIO:= obter_valor_conta(nr_interno_conta_w,0);
else 
	NEW.VL_CONTA_ESTAGIO:= 0;
end if;
 
RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cta_pendencia_hist_atual() FROM PUBLIC;

CREATE TRIGGER cta_pendencia_hist_atual
	BEFORE INSERT ON cta_pendencia_hist FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cta_pendencia_hist_atual();

