SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cta_pendencia_pend_atual ON cta_pendencia CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cta_pendencia_pend_atual() RETURNS trigger AS $BODY$
declare
 
qt_reg_w		smallint;
nm_usuario_w		varchar(15);
ie_tipo_w		varchar(15);
gerar_pend_w		varchar(15);
qt_regra_etapa_w	bigint;
 
BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
 
if (coalesce(Obter_Valor_Param_Usuario(7042,22,obter_Perfil_Ativo,NEW.nm_usuario,wheb_usuario_pck.get_cd_estabelecimento),'N') = 'S') then 
	select	max(CASE WHEN ie_tipo_estagio='EA' THEN 'PCP'  ELSE 'XPCP' END ) 
	into STRICT	ie_tipo_w 
	from	cta_estagio_pend 
	where	nr_sequencia = NEW.nr_seq_estagio;
 
	CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario);
end if;
 
if (coalesce(OLD.nr_seq_estagio,0) <> NEW.nr_seq_estagio) then 
	 
	if (not TG_OP = 'INSERT') then	 
		select	count(*) 
		into STRICT	qt_regra_etapa_w 
		from	fatur_etapa_alta 
		where	coalesce(ie_evento,'A') = 'L';
		 
		if (qt_regra_etapa_w > 0) then 
			CALL gerar_etapa_estagio_pend(NEW.nr_atendimento, NEW.nr_interno_conta, NEW.nr_seq_estagio, NEW.dt_fechamento, 'L', 
					NEW.nm_usuario);
		end if;	
	else 
		select	count(*) 
		into STRICT	qt_regra_etapa_w 
		from	fatur_etapa_alta 
		where	coalesce(ie_evento,'A') = 'M';
		 
		if (qt_regra_etapa_w > 0) then 
			CALL gerar_etapa_estagio_pend(NEW.nr_atendimento, NEW.nr_interno_conta, NEW.nr_seq_estagio, NEW.dt_fechamento, 'M', 
					NEW.nm_usuario);
		end if;	
	end if;
	 
end if;
 
<<Final>> 
qt_reg_w	:= 0;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cta_pendencia_pend_atual() FROM PUBLIC;

CREATE TRIGGER cta_pendencia_pend_atual
	AFTER INSERT OR UPDATE ON cta_pendencia FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cta_pendencia_pend_atual();

