DROP TRIGGER IF EXISTS ctb_documento_after_insert ON ctb_documento CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ctb_documento_after_insert() RETURNS trigger AS $BODY$
declare

   ie_job_w                varchar(25);
   ds_action_w             varchar(255);
   pragma autonomous_transaction;

BEGIN
   if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
   ie_job_w := 'DOC_PENDENTE_' || NEW.nr_sequencia;

   ds_action_w := 'BEGIN '||
                    ' philips_param_pck.set_nr_seq_idioma('||coalesce(philips_param_pck.get_nr_seq_idioma,1)||');'||
                    ' CTB_CONTAB_ONL_LOTE_FIN_PCK.CTB_CONTABILIZA_DOCUMENTO(null,'''
                   || NEW.nm_usuario || ''',' || NEW.nr_sequencia ||'); END;';

   --Criar job para processo da contabilizacao on line

   dbms_scheduler.create_job(job_name   => ie_job_w,
                             job_type   => 'PLSQL_BLOCK',
                             job_action => ds_action_w,
                             start_date => LOCALTIMESTAMP,
                             auto_drop  => TRUE,
                             enabled    => TRUE,
                             comments   => 'Contabilidade On Line de ctb_documento');
    end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ctb_documento_after_insert() FROM PUBLIC;

CREATE TRIGGER ctb_documento_after_insert
	AFTER INSERT ON ctb_documento FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ctb_documento_after_insert();

