CREATE OR REPLACE FUNCTION trigger_fct_ctb_lib_orc_conta_atual() RETURNS trigger AS $BODY$
declare

ie_conta_vigente_w	varchar(1);

BEGIN

select	substr(obter_se_conta_vigente(NEW.cd_conta_contabil, LOCALTIMESTAMP),1,1)
into STRICT	ie_conta_vigente_w
;
if (ie_conta_vigente_w = 'N') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(232821,'CD_CONTA_CONTABIL_W=' ||NEW.cd_conta_contabil);
end if;

RETURN NEW;
END
