SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ctb_modelo_rubrica_atual ON ctb_modelo_rubrica CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ctb_modelo_rubrica_atual() RETURNS trigger AS $BODY$
declare

cd_registro_sped_w	varchar(255);
ie_origem_valor_w	varchar(15);

BEGIN

ie_origem_valor_w	:= NEW.ie_origem_valor;

select	coalesce(max(cd_registro_sped),'X')
into STRICT	cd_registro_sped_w
from	ctb_modelo_relat
where	nr_sequencia	= NEW.nr_seq_modelo;

if (cd_registro_sped_w <> 'X') and (ie_origem_valor_w in ('M','MAC','MA','MAD','MASE','MC','MCSE','MD','S','SC')) then
	CALL ctb_gerar_conta_rubrica(	NEW.nr_sequencia,	NEW.ds_origem,
					NEW.ie_origem_valor, cd_registro_sped_w, 'CTB',	null, NEW.nm_usuario, NEW.nr_seq_modelo);
end if;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ctb_modelo_rubrica_atual() FROM PUBLIC;

CREATE TRIGGER ctb_modelo_rubrica_atual
	AFTER INSERT OR UPDATE ON ctb_modelo_rubrica FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ctb_modelo_rubrica_atual();

