SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ctb_regra_lote_dist_custo_bf ON ctb_regra_lote_dist_custo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ctb_regra_lote_dist_custo_bf() RETURNS trigger AS $BODY$
declare
nr_sequencia_w	ctb_regra_lote_dist_custo.nr_sequencia%type;

pragma autonomous_transaction;

BEGIN

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	ctb_regra_lote_dist_custo a
	where	a.nr_sequencia <> NEW.nr_sequencia
	and	a.cd_empresa = NEW.cd_empresa
	and	((NEW.dt_inicio_vigencia between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,LOCALTIMESTAMP)) or -- inicio vigencia entre a vigencia de outra regra
	(coalesce(NEW.dt_fim_vigencia,LOCALTIMESTAMP) between a.dt_inicio_vigencia and coalesce(a.dt_fim_vigencia,LOCALTIMESTAMP)) or -- final vigencia entre outra regra
	(NEW.dt_inicio_vigencia < a.dt_inicio_vigencia) and (coalesce(NEW.dt_fim_vigencia,LOCALTIMESTAMP) > a.dt_fim_vigencia)) -- range de datas maior que outra regra
	and	coalesce(a.cd_estabelecimento,coalesce(NEW.cd_estabelecimento,0)) = coalesce(NEW.cd_estabelecimento,0);

	if (NEW.dt_inicio_vigencia > NEW.dt_fim_vigencia) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1105269);
	end if;

	if (coalesce(nr_sequencia_w,0) > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1105268);
	end if;
	
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ctb_regra_lote_dist_custo_bf() FROM PUBLIC;

CREATE TRIGGER ctb_regra_lote_dist_custo_bf
	AFTER INSERT OR UPDATE ON ctb_regra_lote_dist_custo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ctb_regra_lote_dist_custo_bf();

