SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS cus_amortizacao_atual ON cus_amortizacao CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_cus_amortizacao_atual() RETURNS trigger AS $BODY$
declare

vl_mensal_w			double precision;

BEGIN

NEW.dt_referencia		:= trunc(NEW.dt_referencia, 'month');
NEW.dt_prim_mes		:= trunc(NEW.dt_prim_mes, 'month');

if (NEW.vl_amortizar <= 0) then
	/*O valor amortizar deve ser maior que zero');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266637);
end if;

if (NEW.qt_mes < 2)  then
	/*O numero de meses deve ser maior que 1;*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266638);
end if;

if (NEW.vl_amortizar <> coalesce(OLD.vl_amortizar,0)) or (NEW.qt_mes <> OLD.qt_mes) then
	vl_mensal_w			:= round((NEW.vl_amortizar / NEW.qt_mes)::numeric,2);
	NEW.vl_prim_mes		:= NEW.vl_amortizar - (vl_mensal_w * (NEW.qt_mes - 1));
	NEW.vl_mensal		:= vl_mensal_w;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_cus_amortizacao_atual() FROM PUBLIC;

CREATE TRIGGER cus_amortizacao_atual
	BEFORE INSERT OR UPDATE ON cus_amortizacao FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_cus_amortizacao_atual();

