CREATE OR REPLACE FUNCTION trigger_fct_declaracao_morte_encef_atual() RETURNS trigger AS $BODY$
declare
dt_nascimento_w		timestamp;
BEGIN

select	max(a.dt_nascimento)
into STRICT	dt_nascimento_w
from	pessoa_fisica a,
		atendimento_paciente b
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and		b.nr_atendimento	= NEW.nr_atendimento;

if (dt_nascimento_w	is not null) and (NEW.DT_DECLARACAO is not null) and (NEW.DT_DECLARACAO	< dt_nascimento_w)then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356157);
end if;


if (NEW.DT_DECLARACAO is not null) and (NEW.DT_AVALIACAO_1 is not null) and (NEW.DT_AVALIACAO_1	> NEW.DT_DECLARACAO) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356162);
end if;


if (NEW.DT_DECLARACAO is not null) and (NEW.DT_AVALIACAO_2 is not null) and (NEW.DT_AVALIACAO_2	> NEW.DT_DECLARACAO) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356162);
end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
