SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS declaracao_obito_atual ON declaracao_obito CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_declaracao_obito_atual() RETURNS trigger AS $BODY$
declare
qt_reg_w	bigint;
nr_atendimento_w	bigint;
dt_nascimento_w		timestamp;

pragma autonomous_transaction;
BEGIN
  BEGIN

if (coalesce(wheb_usuario_pck.get_ie_executar_trigger, 'S') <> 'N')  then

if (NEW.dt_obito is not null) and (NEW.dt_obito >= LOCALTIMESTAMP) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(235412);
end if;

if (coalesce(OLD.DT_OBITO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_OBITO) and (NEW.DT_OBITO is not null) then
	NEW.ds_utc		:= obter_data_utc(NEW.DT_OBITO, 'HV');	
end if;

if (coalesce(OLD.DT_LIBERACAO,LOCALTIMESTAMP + interval '10 days') <> NEW.DT_LIBERACAO) and (NEW.DT_LIBERACAO is not null) then
	NEW.ds_utc_atualizacao	:= obter_data_utc(NEW.DT_LIBERACAO,'HV');
end if;

if (NEW.dt_liberacao is not null) and (OLD.dt_liberacao is null) then
	BEGIN
		select	nr_atendimento
		into STRICT	nr_atendimento_w
		from	declaracao_obito
		where	nr_declaracao = NEW.nr_declaracao
		and		nr_atendimento <> NEW.nr_atendimento
		and	coalesce(ie_situacao,'A') = 'A'  LIMIT 1;
	exception
	when 	no_data_found then
			nr_atendimento_w := 0;
	end;

	if (nr_atendimento_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(70998,'ITEM='||to_char(nr_atendimento_w));
	end if;
END IF;


select	max(a.dt_nascimento)
into STRICT	dt_nascimento_w
from	pessoa_fisica a,
		atendimento_paciente b
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and		b.nr_atendimento	= NEW.nr_atendimento;

if (dt_nascimento_w	is not null) and (NEW.DT_OBITO is not null) and (NEW.DT_OBITO	< dt_nascimento_w)then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(356157);
end if;

CALL atualizar_dados_obito_pac(NEW.dt_liberacao, OLD.dt_liberacao, NEW.nm_usuario, NEW.nr_atendimento, NEW.dt_obito, OLD.dt_obito, NEW.cd_cid_direta, NEW.ie_rn);

if (coalesce(NEW.NR_DECLARACAO,'0') <> coalesce(OLD.NR_DECLARACAO,'0')) and (length(somente_numero(NEW.NR_DECLARACAO))	= length(NEW.NR_DECLARACAO)) and (length(NEW.NR_DECLARACAO) <= 10) then
	
	
	BEGIN
	update	REGRA_NUMERACAO_DEC_ITEM a
	set	IE_DISPONIVEL = 'S'
	where	nr_declaracao	= OLD.NR_DECLARACAO
	and	exists (	SELECT	1
				from	regra_numeracao_declaracao b
				where	a.NR_SEQ_REGRA_NUM	= b.nr_sequencia
				and	b.ie_situacao	= 'A'
				and	b.ie_tipo_numeracao	= 'DO');
	exception
		when others then
		null;
	end;
	
	
	select	count(*)
	into STRICT	qt_reg_w
	from	regra_numeracao_declaracao a,
		REGRA_NUMERACAO_DEC_ITEM b
	where	a.nr_sequencia	= b.NR_SEQ_REGRA_NUM	
	and	nr_declaracao	= NEW.NR_DECLARACAO
	and	IE_DISPONIVEL	<> 'S'
	and	a.ie_situacao	= 'A'
	and	a.ie_tipo_numeracao	= 'DO';
	
			
	if (qt_reg_w	> 0)  then
		-- Numera??o DO j? utilizada / Cancelada. #@#@

		CALL Wheb_mensagem_pck.exibir_mensagem_abort(263595);
	end if;
	
	update	REGRA_NUMERACAO_DEC_ITEM a
	set	IE_DISPONIVEL = 'N'
	where	nr_declaracao	= NEW.NR_DECLARACAO
	and	exists (	SELECT	1
				from	regra_numeracao_declaracao b
				where	a.NR_SEQ_REGRA_NUM	= b.nr_sequencia
				and	b.ie_situacao	= 'A'
				and	b.ie_tipo_numeracao	= 'DO');
end if;

commit;
end if;
  END;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_declaracao_obito_atual() FROM PUBLIC;

CREATE TRIGGER declaracao_obito_atual
	BEFORE INSERT OR UPDATE ON declaracao_obito FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_declaracao_obito_atual();

