DROP TRIGGER IF EXISTS declaracao_obito_delete ON declaracao_obito CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_declaracao_obito_delete() RETURNS trigger AS $BODY$
declare

nr_log_seq_w  bigint;
ie_inativacao_w  varchar(1);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S')  then
if (OLD.NR_DECLARACAO is not null) and (length(somente_numero(OLD.NR_DECLARACAO)) = length(OLD.NR_DECLARACAO)) then
 update REGRA_NUMERACAO_DEC_ITEM a
 set IE_DISPONIVEL = 'S'
 where nr_declaracao = OLD.NR_DECLARACAO
 and exists ( SELECT 1
    from regra_numeracao_declaracao b
    where a.NR_SEQ_REGRA_NUM = b.nr_sequencia
    and b.ie_situacao = 'A'
    and b.ie_tipo_numeracao = 'DO');
end if;


select  nextval('log_alteracao_prontuario_seq')
into STRICT  nr_log_seq_w
;

insert into log_alteracao_prontuario(nr_sequencia,
          dt_atualizacao,
          ds_evento,
          ds_maquina,
          cd_pessoa_fisica,
          ds_item,
          nr_atendimento,
          dt_liberacao,
          dt_inativacao,
		  nm_usuario_nrec,
          nm_usuario) values (nr_log_seq_w,
          OLD.DT_OBITO,
          obter_desc_expressao(493387) ,
          wheb_usuario_pck.get_nm_maquina,
          obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario,'C'),
          obter_desc_expressao(314115),
           OLD.nr_atendimento,
          OLD.dt_liberacao,
          OLD.dt_inativacao,
		  OLD.nm_usuario_nrec,
          coalesce(wheb_usuario_pck.get_nm_usuario, OLD.nm_usuario));

end if;
RETURN OLD;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
