SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS declaracao_obito_pend_atual ON declaracao_obito CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_declaracao_obito_pend_atual() RETURNS trigger AS $BODY$
declare
 
qt_reg_w		smallint;
ie_tipo_w		varchar(10);
ie_lib_declaracao_w	varchar(10);
nr_seq_reg_elemento_w	bigint;
BEGIN
  BEGIN 
if (wheb_usuario_pck.get_ie_executar_trigger	= 'N') then 
	goto Final;
end if;
 
select max(IE_LIB_OBITO) 
into STRICT	  ie_lib_declaracao_w 
from	  parametro_medico 
where  cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;
 
BEGIN 
 
	if (TG_OP = 'INSERT') or (TG_OP = 'UPDATE') then 
 
		if (coalesce(ie_lib_declaracao_w,'N') = 'S') then 
			if (NEW.dt_liberacao is null) then 
				ie_tipo_w := 'DOB';
			elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then 
				ie_tipo_w := 'XDOB';
			end if;
 
 
			if (ie_tipo_w	is not null) then 
				CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.nr_sequencia, obter_pessoa_atendimento(NEW.nr_atendimento,'C'), NEW.nr_atendimento, NEW.nm_usuario,'L',null,null,null,null,null,null,null,null,null,null,null,null,null,null,nr_seq_reg_elemento_w);
			end if;
 
			 
		end if;
	elsif (TG_OP = 'DELETE') then 
	 
		delete 	from pep_item_pendente 
		where 	IE_TIPO_REGISTRO = 'DOB' 
		and	nr_seq_registro = OLD.nr_sequencia 
		and	coalesce(IE_TIPO_PENDENCIA,'L')	= 'L';
		 
		commit;
 
	end if;
 
exception 
when others then 
	null;
end;
 
<<Final>> 
qt_reg_w	:= 0;
  END;
IF TG_OP = 'DELETE' THEN
	RETURN OLD;
ELSE
	RETURN NEW;
END IF;

end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_declaracao_obito_pend_atual() FROM PUBLIC;

CREATE TRIGGER declaracao_obito_pend_atual
	AFTER INSERT OR UPDATE OR DELETE ON declaracao_obito FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_declaracao_obito_pend_atual();

