CREATE OR REPLACE FUNCTION trigger_fct_delete_cid_med_certificate() RETURNS trigger AS $BODY$
DECLARE
nr_seq_atestado_paciente_w  cid_atestado_paciente.nr_seq_atestado_paciente%type;

BEGIN

IF (wheb_usuario_pck.get_ie_executar_trigger = 'S' AND coalesce(pkg_i18n.get_user_locale, 'pt_BR') = 'es_CO') THEN
	SELECT	coalesce(max(nr_seq_atestado_paciente), 0)
