	FROM	cid_atestado_paciente
	WHERE	nr_seq_atestado_paciente = OLD.nr_sequencia;

	IF (nr_seq_atestado_paciente_w > 0) THEN
		DELETE 
		FROM    cid_atestado_paciente
		WHERE	nr_seq_atestado_paciente = nr_seq_atestado_paciente_w;
	END IF;
END IF;
RETURN OLD;
END
$BODY$
