SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS delete_model_reference ON workplace_my_models CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_delete_model_reference() RETURNS trigger AS $BODY$
DECLARE
  i RECORD;

BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then

    for i in (SELECT nr_sequencia seq from workplace_models_cards where nr_seq_model = OLD.NR_SEQUENCIA) loop
        
        delete from workplace_models_cards
        where nr_sequencia = i.seq
        and nr_seq_model = OLD.NR_SEQUENCIA;

    end loop;

    for i in (SELECT nr_sequencia seq from workplace_models_lib where nr_seq_model = OLD.NR_SEQUENCIA) loop
        
        delete from workplace_models_lib
        where nr_sequencia = i.seq
        and nr_seq_model = OLD.NR_SEQUENCIA;

    end loop;

end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_delete_model_reference() FROM PUBLIC;

CREATE TRIGGER delete_model_reference
	AFTER DELETE ON workplace_my_models FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_delete_model_reference();

