DROP TRIGGER IF EXISTS delete_oc_item_entrega ON ordem_compra_item_entrega CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_delete_oc_item_entrega() RETURNS trigger AS $BODY$
declare

nr_sequencia_w		inspecao_recebimento.nr_sequencia%type;

BEGIN
select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	inspecao_recebimento
where	nr_seq_entrega = OLD.nr_sequencia;

if (nr_sequencia_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(311804,'NR_SEQ_INSP_W='||nr_sequencia_w||';DS_STACK_W='||substr(dbms_utility.format_call_stack,1,2000));
end if;

RETURN OLD;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
