CREATE OR REPLACE FUNCTION trigger_fct_desenv_acordo_os_before() RETURNS trigger AS $BODY$
declare

BEGIN
if	(TG_OP = 'UPDATE' and (OLD.ie_status_acordo = 'I') and
	((NEW.ie_status_acordo = 'A') or (NEW.ie_status_acordo = 'R'))) then
	NEW.dt_posicionamento	:= LOCALTIMESTAMP;
end if;

if (not(substr(dbms_utility.format_call_stack,1,4000) like '%MAN_ORDEM_SERVICO_ATUAL%'))then
	update	man_ordem_servico
	set 	dt_externa_acordo	= NEW.dt_entrega_prev,
		dt_atualizacao		= LOCALTIMESTAMP,
		nm_usuario		= wheb_usuario_pck.get_nm_usuario
	where 	nr_sequencia		= NEW.nr_seq_ordem_servico;
end if;

RETURN NEW;
end
