DROP TRIGGER IF EXISTS dicionario_atual ON dicionario CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_dicionario_atual() RETURNS trigger AS $BODY$
BEGIN
	/* Coelho em 26/09/08 	OS110150 */

if	((NEW.ds_palavra <> OLD.ds_palavra) or (OLD.ds_fonetica is null )) then
	NEW.ds_fonetica := gera_fonetica(NEW.ds_palavra,'N');
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_dicionario_atual() FROM PUBLIC;

CREATE TRIGGER dicionario_atual
	BEFORE INSERT OR UPDATE ON dicionario FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_dicionario_atual();

