DROP TRIGGER IF EXISTS drg_calc_rule_proc_atual ON drg_calc_rule_proc_cat CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_drg_calc_rule_proc_atual() RETURNS trigger AS $BODY$
declare
cd_procedimento_w	procedimento.cd_procedimento%type;
BEGIN

--We dont know why, but the locator is not setting the CD_PROCEDIMENTO properly
if (wheb_usuario_pck.get_ie_executar_trigger <> 'N') then
if (coalesce(NEW.CD_PROCEDIMENTO_LOC,'XPTO') <> coalesce(OLD.CD_PROCEDIMENTO_LOC,'XPTO') ) then

	select	max(cd_procedimento)
	into STRICT	cd_procedimento_w
	from	procedimento
	where	cd_procedimento_loc = NEW.cd_procedimento_loc
	and	ie_situacao = 'A'
	and	ie_origem_proced = NEW.ie_origem_proced;

	if (cd_procedimento_w is not null) then
		NEW.cd_procedimento	:= cd_procedimento_w;
	end if;

end if;
end if;


RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
