CREATE OR REPLACE FUNCTION trigger_fct_ehr_registro_insert() RETURNS trigger AS $BODY$
declare
 
BEGIN 
if (NEW.cd_perfil_ativo is null) and (obter_perfil_ativo 	> 0)then 
	NEW.cd_perfil_ativo	:= obter_perfil_ativo;
end if;
 
NEW.cd_unidade := coalesce(Obter_Ult_Unid_Atendimento(NEW.nr_atendimento),0);
 
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
