SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ehr_registro_pend_atual ON ehr_registro CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ehr_registro_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w	smallint;
nm_usuario_w    varchar(15);

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'N')  then
	goto Final_End;
end if;

select	obter_usuario_pf(NEW.cd_profissional)
into STRICT	nm_usuario_w
;	

if (coalesce(NEW.IE_AVALIADOR_AUX,'N') = 'S') and (NEW.DT_LIBERACAO_AUX is not null) then
	
	if (NEW.dt_liberacao is null) then
		Update  pep_item_pendente
		set	nm_usuario = nm_usuario_w
		where	NR_SEQ_EHR_REG = NEW.nr_sequencia;
	end if;

else
		
	if (NEW.dt_liberacao is null) and (not TG_OP = 'DELETE') and (NEW.nr_seq_templ is not null) then				    
    CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => 'EHR',
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => NEW.cd_paciente, 
      nr_atendimento_p => NEW.nr_atendimento, 
      nm_usuario_p => nm_usuario_w,
      nr_atend_cons_pepa_p=> NEW.nr_seq_atend_cons_pepa
    );
	
	elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
    CALL Gerar_registro_pendente_PEP(
      cd_tipo_registro_p => 'XEHR',
      nr_sequencia_registro_p => NEW.nr_sequencia,
      cd_pessoa_fisica_p => NEW.cd_paciente,
      nr_atendimento_p => NEW.nr_atendimento, 
      nm_usuario_p => nm_usuario_w,
      nr_atend_cons_pepa_p=> NEW.nr_seq_atend_cons_pepa
    );
	
	end if;

end if;

<<Final_End>>
qt_reg_w := 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ehr_registro_pend_atual() FROM PUBLIC;

CREATE TRIGGER ehr_registro_pend_atual
	AFTER INSERT OR UPDATE ON ehr_registro FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ehr_registro_pend_atual();

