CREATE OR REPLACE FUNCTION trigger_fct_ehr_reg_elemento_atual() RETURNS trigger AS $BODY$
declare
dt_liberacao_w		timestamp;
BEGIN
  BEGIN

if (wheb_usuario_pck.get_ie_executar_trigger	= 'S')  then

	select	max(dt_liberacao)
	into STRICT	dt_liberacao_w
	from	ehr_reg_template
	where	nr_sequencia	= NEW.NR_SEQ_REG_TEMPLATE;

	if (dt_liberacao_w	is not null and NEW.dt_liberacao is null) or (NEW.dt_liberacao is not null and OLD.dt_liberacao is not null) then
		CALL exibir_erro_abortar(obter_desc_expressao(518092)||' #@#@',null);
	end if;



		BEGIN


		if (TG_OP = 'INSERT') or
			((NEW.DT_RESULTADO	<> OLD.DT_RESULTADO) or (NEW.DS_RESULTADO	<> OLD.DS_RESULTADO) or (NEW.VL_RESULTADO	<> OLD.VL_RESULTADO)) then

		NEW.DS_CALL_STACK	:= substr(dbms_utility.format_call_stack,1,1000);
		end if;
		exception
			when others then
			null;
		end;
end if;

  END;
RETURN NEW;
end
