SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ehr_reg_template_pend_atual ON ehr_reg_template CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ehr_reg_template_pend_atual() RETURNS trigger AS $BODY$
declare

qt_reg_w					smallint;
ie_tipo_w					varchar(10);
ie_liberar_ganho_perda_w	varchar(10);
cd_paciente_w				varchar(10);
nr_atendimento_w			bigint;
ie_gerar_assinatura_w		varchar(1) := 'S';

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'N')  then
	goto Final;
end if;

if (NEW.dt_liberacao is null) then
	ie_tipo_w := 'EHR';
elsif (OLD.dt_liberacao is null) and (NEW.dt_liberacao is not null) then
	ie_tipo_w := 'XEHR';
end if;

if (ie_tipo_w is not null) then
	if (obter_funcao_ativa = 10026) then
		ie_gerar_assinatura_w := 'S';
	elsif (obter_funcao_ativa = 874) then
		ie_gerar_assinatura_w := 'N';	
	end if;

	if (ie_gerar_assinatura_w = 'S') then
		select	cd_paciente,
				nr_atendimento
		into STRICT	cd_paciente_w,
				nr_atendimento_w
		from	ehr_registro
		where	nr_sequencia = NEW.NR_SEQ_REG;
		
		CALL Gerar_registro_pendente_PEP(ie_tipo_w, NEW.NR_SEQ_REG, cd_paciente_w, nr_atendimento_w, NEW.nm_usuario);
	end if;
end if;

<<Final>>
qt_reg_w := 0;
RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ehr_reg_template_pend_atual() FROM PUBLIC;

CREATE TRIGGER ehr_reg_template_pend_atual
	AFTER INSERT OR UPDATE ON ehr_reg_template FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ehr_reg_template_pend_atual();

