SET client_encoding TO 'UTF8';

DROP TRIGGER IF EXISTS ehr_template_conteudo_atual ON ehr_template_conteudo CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ehr_template_conteudo_atual() RETURNS trigger AS $BODY$
declare
NR_SEQ_TIPO_DADO_w	bigint;
qtd_w bigint;

BEGIN

if (NEW.DS_MASCARA is not null) and (NEW.NR_SEQ_ELEMENTO is not null) and (position('#' in NEW.DS_MASCARA)	> 0)	then
	BEGIN
	
	select	max(NR_SEQ_TIPO_DADO)
	into STRICT	NR_SEQ_TIPO_DADO_w
	from	ehr_elemento
	where	nr_sequencia	= NEW.NR_SEQ_ELEMENTO;
	
	
	if (NR_SEQ_TIPO_DADO_w in (4,5)) then
		BEGIN
		CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(270209);
		end;
	end if;
	
	end;
end if;

  if ((TG_OP = 'INSERT' AND NEW.nr_seq_elemento = 1613 and NEW.nr_seq_linked_data is not null) or
      ((TG_OP = 'UPDATE') and (NEW.nr_seq_elemento = 1613 and NEW.nr_seq_linked_data is not null and (NEW.nr_seq_linked_data <> OLD.nr_seq_linked_data))))	then

    select  count(1)
    into STRICT    qtd_w
    from    ehr_template_conteudo 
    where   nr_sequencia <> NEW.nr_sequencia
	and		nr_seq_template = NEW.nr_seq_template and nr_seq_linked_data = NEW.nr_seq_linked_data;

    if (qtd_w > 0) then
      CALL wheb_mensagem_pck.exibir_mensagem_abort(1131742);
    end if;

    IF TG_OP = 'INSERT' THEN
       CALL ehr_alterar_tabela_linked(NEW.nr_seq_template, NEW.nr_seq_linked_data, 'CREATE');
    END IF;

  end if;

RETURN NEW;
end;
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_ehr_template_conteudo_atual() FROM PUBLIC;

CREATE TRIGGER ehr_template_conteudo_atual
	BEFORE INSERT OR UPDATE ON ehr_template_conteudo FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_ehr_template_conteudo_atual();

