DROP TRIGGER IF EXISTS ehr_template_delete ON ehr_template CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_ehr_template_delete() RETURNS trigger AS $BODY$
declare
ie_registro_filho_w  	varchar(1);
pragma autonomous_transaction;

BEGIN

select 	coalesce(max('S'),'N')
into STRICT	ie_registro_filho_w
from	ehr_template_conteudo
where	nr_seq_template = OLD.nr_sequencia;

if (ie_registro_filho_w = 'S') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(687932);
end	if;

RETURN OLD;
end
