DROP TRIGGER IF EXISTS eme_chamado_insert ON eme_chamado CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_eme_chamado_insert() RETURNS trigger AS $BODY$
DECLARE

vl_custo_w	double precision;
km_minima_w	bigint;

BEGIN

vl_custo_w:= obter_eme_valor_custo_km(NEW.nr_seq_veiculo, NEW.nr_seq_custo_km);

NEW.vl_km_unit		:= vl_custo_w;
if (NEW.qt_km_saida is not null) and (NEW.qt_km_retorno is not null)  then
	NEW.qt_km_percorrido	:= NEW.qt_km_retorno - NEW.qt_km_saida;
end if;

km_minima_w := 	obter_eme_km_minima(NEW.nr_seq_veiculo, NEW.nr_seq_custo_km);

if (km_minima_w is not null) then

	if (NEW.qt_km_percorrido < km_minima_w) then
		NEW.qt_km_cobrar := 0;
	else
		NEW.qt_km_cobrar	:= round(NEW.qt_km_percorrido - km_minima_w);
	end if;
	NEW.vl_km_total	:= NEW.qt_km_cobrar * NEW.vl_km_unit;
else
	NEW.qt_km_cobrar		:= NEW.qt_km_percorrido;
	NEW.vl_km_total		:= NEW.qt_km_percorrido * NEW.vl_km_unit;
end if;

RETURN NEW;
END
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
-- REVOKE ALL ON FUNCTION trigger_fct_eme_chamado_insert() FROM PUBLIC;

CREATE TRIGGER eme_chamado_insert
	BEFORE INSERT OR UPDATE ON eme_chamado FOR EACH ROW
	EXECUTE PROCEDURE trigger_fct_eme_chamado_insert();

