CREATE OR REPLACE FUNCTION trigger_fct_eme_regra_envio_email_atual() RETURNS trigger AS $BODY$
declare
qt_reg_emissao_boleto_w 	bigint	:= 0;
BEGIN

if (NEW.ie_tipo_email = 'E') then

	select count(*)
	into STRICT qt_reg_emissao_boleto_w
	from eme_regra_envio_email
	where ie_tipo_email = 'E'
	and nr_sequencia <> NEW.nr_sequencia;

	if (qt_reg_emissao_boleto_w > 0) then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(1142147);
	end if;
end if;

RETURN NEW;
end
