DROP TRIGGER IF EXISTS emprestimo_material_dev_atual ON emprestimo_material_dev CASCADE;
CREATE OR REPLACE FUNCTION trigger_fct_emprestimo_material_dev_atual() RETURNS trigger AS $BODY$
declare
cd_local_estoque_w	smallint;
cd_estabelecimento_w	smallint;
cd_material_w		integer;
ie_consiste_inv_w	varchar(1);
ie_bloqueado_inventario_w	varchar(1);
ie_estoque_lote_w	varchar(1);
ie_tipo_w		emprestimo.ie_tipo%type;

BEGIN
select	cd_estabelecimento,
	cd_local_estoque,
	ie_tipo
into STRICT	cd_estabelecimento_w,
	cd_local_estoque_w,
	ie_tipo_w
from	emprestimo
where	nr_emprestimo = NEW.nr_emprestimo;

ie_consiste_inv_w := Obter_Param_Usuario(143, 232, obter_perfil_ativo, NEW.nm_usuario, cd_estabelecimento_w, ie_consiste_inv_w);
ie_estoque_lote_w := substr(obter_se_material_estoque_lote(cd_estabelecimento_w, NEW.cd_material),1,1);

if (ie_estoque_lote_w = 'S') and (NEW.nr_seq_lote is null) and (ie_tipo_w = 'E') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(205365,'CD_MATERIAL=' || NEW.cd_material);
end if;

if (ie_consiste_inv_w = 'S') then
	BEGIN
	select	cd_material
	into STRICT	cd_material_w
	from	emprestimo_material
	where	nr_emprestimo = NEW.nr_emprestimo
	and	nr_sequencia = NEW.nr_sequencia;

	ie_bloqueado_inventario_w := substr(Obter_Se_Material_Bloqueio_Inv(cd_estabelecimento_w, cd_material_w, cd_local_estoque_w),1,1);

	if (ie_bloqueado_inventario_w = 'S') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(205366,'CD_MATERIAL=' || NEW.cd_material);
	end if;
	end;
end if;
RETURN NEW;
end
$BODY$
 LANGUAGE 'plpgsql' SECURITY DEFINER;
