CREATE OR REPLACE FUNCTION trigger_fct_episodio_acompanhante_after() RETURNS trigger AS $BODY$
declare
reg_integracao_p	gerar_int_padrao.reg_integracao;
nr_seq_documento_w	intpd_fila_transmissao.nr_seq_documento%type;
nm_usuario_w		episodio_acompanhante.nm_usuario%type;

function get_pessoa_episodio(
		nr_sequencia_p	episodio_acompanhante.nr_seq_ep_paciente%type)
		return text is

cd_pessoa_fisica_w	episodio_paciente.cd_pessoa_fisica%type;

pragma autonomous_transaction;
BEGIN
  BEGIN
BEGIN
select	cd_pessoa_fisica
into STRICT	cd_pessoa_fisica_w
from	episodio_paciente
where	nr_sequencia = nr_sequencia_p;
exception
when others then
	cd_pessoa_fisica_w	:=	null;
end;

return cd_pessoa_fisica_w;

end;

BEGIN
if (wheb_usuario_pck.get_ie_executar_trigger = 'S') then
	BEGIN
	reg_integracao_p.ie_status		:= 'P';

	if (TG_OP = 'INSERT') then
		reg_integracao_p.ie_operacao		:= 	'I';
		nr_seq_documento_w			:=	NEW.nr_seq_ep_paciente || '|' || NEW.nr_seq_ep_acompanhante;
		reg_integracao_p.cd_pessoa_fisica	:=	get_pessoa_episodio(NEW.nr_seq_ep_paciente);
		nm_usuario_w				:=	NEW.nm_usuario;
	elsif (TG_OP = 'UPDATE') then
		reg_integracao_p.ie_operacao		:= 	'A';
		nr_seq_documento_w			:=	NEW.nr_seq_ep_paciente || '|' || NEW.nr_seq_ep_acompanhante;
		reg_integracao_p.cd_pessoa_fisica	:=	get_pessoa_episodio(NEW.nr_seq_ep_paciente);
