	elsif (TG_OP = 'DELETE') then
		reg_integracao_p.ie_operacao		:= 	'E';
		nr_seq_documento_w			:=	OLD.nr_seq_ep_paciente || '|' || OLD.nr_seq_ep_acompanhante;
		reg_integracao_p.cd_pessoa_fisica	:=	get_pessoa_episodio(OLD.nr_seq_ep_paciente);
		nm_usuario_w				:=	OLD.nm_usuario;
	end if;

	reg_integracao_p := gerar_int_padrao.gravar_integracao('273', nr_seq_documento_w, nm_usuario_w, reg_integracao_p);
	end;
end if;

  END;
IF TG_OP = 'DELETE' THEN
